// @ts-check
import { defineConfig } from 'astro/config';
import tailwind from '@astrojs/tailwind';
import mdx from '@astrojs/mdx';

// https://astro.build/config
export default defineConfig({
  integrations: [
    tailwind(),
    mdx()
  ],
  markdown: {
    shikiConfig: {
      themes: {
        light: 'github-light',
        dark: 'github-dark-default'
      },
      defaultColor: false,
      cssVariablePrefix: '--shiki-',
      wrap: true,
      langs: [
        'javascript',
        'typescript',
        'python',
        'bash',
        'json',
        'html',
        'css',
        'sql',
        'yaml',
        'markdown',
        'mermaid'
      ]
    }
  },
  site: 'https://alqattandev.github.io/myblog', // GitHub Pages URL
  base: '/myblog' // Repository name for GitHub Pages subdirectory
});

/** @type {import('tailwindcss').Config} */
module.exports = {
  content: ['./src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}'],
  darkMode: 'class',
  theme: {
    extend: {
      fontFamily: {
        'serif': ['Playfair Display', 'Georgia', 'serif'],
        'sans': ['Inter', 'system-ui', 'sans-serif'],
      },
      // Typography configuration to properly handle code highlighting
      typography: ({ theme }) => ({
        DEFAULT: {
          css: {
            // 1. Style INLINE code tags.
            // This will apply to `<code>` tags inside paragraphs, lists, etc.
            'code': {
              color: 'var(--theme-code-text)',
              backgroundColor: 'var(--theme-code-bg)',
              padding: '0.2em 0.4em',
              borderRadius: '0.25rem',
              fontWeight: '500',
            },

            // 2. Reset styles for code tags that are INSIDE a pre block.
            'pre code': {
              // Remove the background and padding from the inline code style.
              backgroundColor: 'transparent',
              padding: '0',
              // CRITICAL: Make the text color inherit from the parent <pre> tag.
              // The <pre> tag's color is correctly set by Shiki's variables.
              color: 'inherit',
              fontWeight: 'inherit', // Inherit font weight as well.
            },

            // 3. Disable the default quotation marks prose adds around code blocks.
            'code::before': { content: '""' },
            'code::after': { content: '""' },

            // 4. (Optional but recommended) Let our custom CSS handle the <pre> container.
            'pre': false,
          },
        },
      }),
      colors: {
        primary: {
          DEFAULT: '#0f766e',
          50: '#f0fdfa',
          100: '#ccfbf1',
          200: '#99f6e4',
          300: '#5eead4',
          400: '#2dd4bf',
          500: '#14b8a6',
          600: '#0d9488',
          700: '#0f766e',
          800: '#115e59',
          900: '#134e4a',
        },
        secondary: {
          DEFAULT: '#f8fafc',
          50: '#f8fafc',
          100: '#f1f5f9',
          200: '#e2e8f0',
          300: '#cbd5e1',
          400: '#94a3b8',
          500: '#64748b',
          600: '#475569',
          700: '#334155',
          800: '#1e293b',
          900: '#0f172a',
        },
        accent: {
          DEFAULT: '#14b8a6',
          50: '#f0fdfa',
          100: '#ccfbf1',
          200: '#99f6e4',
          300: '#5eead4',
          400: '#2dd4bf',
          500: '#14b8a6',
          600: '#0d9488',
          700: '#0f766e',
          800: '#115e59',
          900: '#134e4a',
        },
        // Semantic theme-aware colors using CSS custom properties
        theme: {
          'bg-primary': 'var(--theme-bg-primary)',
          'bg-secondary': 'var(--theme-bg-secondary)',
          'bg-tertiary': 'var(--theme-bg-tertiary)',
          'bg-code': 'var(--theme-bg-code)',
          'bg-muted': 'var(--theme-bg-muted)',
          'text-primary': 'var(--theme-text-primary)',
          'text-secondary': 'var(--theme-text-secondary)',
          'text-tertiary': 'var(--theme-text-tertiary)',
          'text-muted': 'var(--theme-text-muted)',
          'text-inverse': 'var(--theme-text-inverse)',
          'border-primary': 'var(--theme-border-primary)',
          'border-secondary': 'var(--theme-border-secondary)',
          'border-muted': 'var(--theme-border-muted)',
          'accent-primary': 'var(--theme-accent-primary)',
          'accent-hover': 'var(--theme-accent-primary-hover)',
          'accent-secondary': 'var(--theme-accent-secondary)',
          'accent-success': 'var(--theme-accent-success)',
          'code-text': 'var(--theme-code-text)',
          'code-bg': 'var(--theme-code-bg)',
          'link': 'var(--theme-link-color)',
          'link-hover': 'var(--theme-link-hover)',
        }
      },
