type=\"module\">const o=document.getElementById(\"theme-toggle\"),d=document.getElementById(\"theme-toggle-light-icon\"),c=document.getElementById(\"theme-toggle-dark-icon\");function a(){return typeof localStorage<\"u\"&&localStorage.getItem(\"theme\")?localStorage.getItem(\"theme\"):window.matchMedia(\"(prefers-color-scheme: dark)\").matches?\"dark\":\"light\"}function n(e){const t=e===\"dark\";t?(document.documentElement.classList.add(\"dark\"),d?.classList.remove(\"hidden\"),c?.classList.add(\"hidden\")):(document.documentElement.classList.remove(\"dark\"),d?.classList.add(\"hidden\"),c?.classList.remove(\"hidden\")),localStorage.setItem(\"theme\",e),document.dispatchEvent(new CustomEvent(\"theme-changed\",{detail:{theme:e,isDark:t}}))}const m=a();n(m);o?.addEventListener(\"click\",()=>{const t=(localStorage.getItem(\"theme\")||\"light\")===\"dark\"?\"light\":\"dark\";n(t)});window.matchMedia(\"(prefers-color-scheme: dark)\").addEventListener(\"change\",e=>{localStorage.getItem(\"theme\")||n(e.matches?\"dark\":\"light\")});</script> </div> </nav> </div> </header> <!-- Main Content --> <main class=\"flex-1\">  <article class=\"mx-auto max-w-4xl px-4 py-12\"> <!-- Article Header --> <header class=\"mb-8 border-b border-gray-200 dark:border-gray-700 pb-8\"> <div class=\"mb-4\"> <h1 class=\"text-4xl font-bold text-gray-900 dark:text-gray-100 sm:text-5xl\">Working with Astro Content Collections</h1> <p class=\"mt-4 text-xl text-gray-600 dark:text-gray-400\">A guide to using Astro&#39;s content collections for type-safe content management</p> </div> <!-- Meta Information --> <div class=\"flex flex-wrap items-center gap-4 text-sm text-gray-500 dark:text-gray-400\"> <div class=\"flex items-center\"> <time datetime=\"2025-07-15T00:00:00.000Z\">July 15, 2025</time>  </div> <div class=\"flex items-center\"> <span>By Ali Alqattan</span> </div> <div class=\"flex flex-wrap gap-2\"> <span class=\"rounded-full bg-primary-100 dark:bg-gray-700 px-3 py-1 text-xs font-medium text-p