/Users/alialqattan/Projects/myblog/logs/pre_tool_use.json:508:      "content": "---\n// Theme toggle component for dark/light mode switching\n---\n\n<button\n  id=\"theme-toggle\"\n  class=\"flex items-center justify-center w-8 h-8 rounded-lg border border-gray-300 bg-white text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 transition-all dark:border-gray-600 dark:bg-gray-800 dark:text-gray-300 dark:hover:bg-gray-700\"\n  aria-label=\"Toggle theme\"\n>\n  <!-- Sun icon (visible in dark mode) -->\n  <svg\n    id=\"theme-toggle-light-icon\"\n    class=\"w-4 h-4 hidden\"\n    fill=\"currentColor\"\n    viewBox=\"0 0 20 20\"\n    xmlns=\"http://www.w3.org/2000/svg\"\n  >\n    <path\n      fill-rule=\"evenodd\"\n      d=\"M10 2a1 1 0 011 1v1a1 1 0 11-2 0V3a1 1 0 011-1zm4 8a4 4 0 11-8 0 4 4 0 018 0zm-.464 4.95l.707.707a1 1 0 001.414-1.414l-.707-.707a1 1 0 00-1.414 1.414zm2.12-10.607a1 1 0 010 1.414l-.706.707a1 1 0 11-1.414-1.414l.707-.707a1 1 0 011.414 0zM17 11a1 1 0 100-2h-1a1 1 0 100 2h1zm-7 4a1 1 0 011 1v1a1 1 0 11-2 0v-1a1 1 0 011-1zM5.05 6.464A1 1 0 106.465 5.05l-.708-.707a1 1 0 00-1.414 1.414l.707.707zm1.414 8.486l-.707.707a1 1 0 01-1.414-1.414l.707-.707a1 1 0 011.414 1.414zM4 11a1 1 0 100-2H3a1 1 0 000 2h1z\"\n      clip-rule=\"evenodd\"\n    ></path>\n  </svg>\n\n  <!-- Moon icon (visible in light mode) -->\n  <svg\n    id=\"theme-toggle-dark-icon\"\n    class=\"w-4 h-4\"\n    fill=\"currentColor\"\n    viewBox=\"0 0 20 20\"\n    xmlns=\"http://www.w3.org/2000/svg\"\n  >\n    <path\n      d=\"M17.293 13.293A8 8 0 016.707 2.707a8.001 8.001 0 1010.586 10.586z\"\n    ></path>\n  </svg>\n</button>\n\n<script>\n  // Theme toggle functionality\n  const themeToggle = document.getElementById('theme-toggle');\n  const lightIcon = document.getElementById('theme-toggle-light-icon');\n  const darkIcon = document.getElementById('theme-toggle-dark-icon');\n\n  // Check for saved theme preference or default to system preference\n  function getThemePreference() {\n    if (typeof localStorage !== 'undefined' && localStorage.getItem('theme')) {\n      return localStorage.getItem('theme');\n    }\n    return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';\n  }\n\n  // Update the theme and icon visibility\n  function updateTheme(theme: string) {\n    const isDark = theme === 'dark';\n    \n    if (isDark) {\n      document.documentElement.classList.add('dark');\n      lightIcon?.classList.remove('hidden');\n      darkIcon?.classList.add('hidden');\n    } else {\n      document.documentElement.classList.remove('dark');\n      lightIcon?.classList.add('hidden');\n      darkIcon?.classList.remove('hidden');\n    }\n    \n    // Save to localStorage\n    localStorage.setItem('theme', theme);\n  }\n\n  // Initialize theme on page load\n  const initialTheme = getThemePreference();\n  updateTheme(initialTheme);\n\n  // Add click event listener\n  themeToggle?.addEventListener('click', () => {\n    const currentTheme = localStorage.getItem('theme') || 'light';\n    const newTheme = currentTheme === 'dark' ? 'light' : 'dark';\n    updateTheme(newTheme);\n  });\n\n  // Listen for system theme changes\n  window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', (e) => {\n    if (!localStorage.getItem('theme')) {\n      updateTheme(e.matches ? 'dark' : 'light');\n    }\n  });\n</script>"
