    <script>
        function toggleToc() {
            const toc = document.getElementById('toc');
            toc.classList.toggle('active');
        }

        function closeTocOnMobile() {
            if (window.innerWidth <= 1024) {
                const toc = document.getElementById('toc');
                toc.classList.remove('active');
            }
        }

        // Close TOC when clicking outside on mobile
        document.addEventListener('click', function(event) {
            const toc = document.getElementById('toc');
            const hamburgerButton = document.querySelector('button[onclick="toggleToc()"]');

            if (window.innerWidth <= 1024 && 
                !toc.contains(event.target) && 
                event.target !== hamburgerButton && 
                !hamburgerButton.contains(event.target)) {
                toc.classList.remove('active');
            }
        });

        // Smooth scrolling for anchor links
        document.querySelectorAll('a[href^="#"]').forEach(anchor => {
            anchor.addEventListener('click', function (e) {
                e.preventDefault();
                const target = document.querySelector(this.getAttribute('href'));
                if (target) {
                    target.scrollIntoView({
                        behavior: 'smooth',
                        block: 'start'
                    });
                    // Close TOC after clicking a link on mobile
                    if (window.innerWidth <= 1024) {
                        const toc = document.getElementById('toc');
                        toc.classList.remove('active');
                    }
                }
            });
        });

        // Highlight active section in TOC
        window.addEventListener('scroll', function() {
            const sections = document.querySelectorAll('section[id]');
            const tocLinks = document.querySelectorAll('#toc a[href^="#"]');

            let current = '';
            sections.forEach(section => {
                const sectionTop = section.offsetTop;
                const sectionHeight = section.clientHeight;
                if (window.scrollY >= sectionTop - 100) {
                    current = section.getAttribute('id');
                }
            });

            tocLinks.forEach(link => {
                link.classList.remove('text-white', 'font-semibold');
                link.classList.add('text-teal-100');
                if (link.getAttribute('href') === '#' + current) {
                    link.classList.remove('text-teal-100');
                    link.classList.add('text-white', 'font-semibold');
                }
            });
        });
    </script>


