          800: '#1e293b',
          900: '#0f172a',
        },
        accent: {
          DEFAULT: '#14b8a6',
          50: '#f0fdfa',
          100: '#ccfbf1',
          200: '#99f6e4',
          300: '#5eead4',
          400: '#2dd4bf',
          500: '#14b8a6',
          600: '#0d9488',
          700: '#0f766e',
          800: '#115e59',
          900: '#134e4a',
        },
        // Semantic theme-aware colors using CSS custom properties
        theme: {
          'bg-primary': 'var(--theme-bg-primary)',
          'bg-secondary': 'var(--theme-bg-secondary)',
          'bg-tertiary': 'var(--theme-bg-tertiary)',
          'bg-code': 'var(--theme-bg-code)',
          'bg-muted': 'var(--theme-bg-muted)',
          'text-primary': 'var(--theme-text-primary)',
          'text-secondary': 'var(--theme-text-secondary)',
          'text-tertiary': 'var(--theme-text-tertiary)',
          'text-muted': 'var(--theme-text-muted)',
          'text-inverse': 'var(--theme-text-inverse)',
          'border-primary': 'var(--theme-border-primary)',
          'border-secondary': 'var(--theme-border-secondary)',
          'border-muted': 'var(--theme-border-muted)',
          'accent-primary': 'var(--theme-accent-primary)',
          'accent-hover': 'var(--theme-accent-primary-hover)',
          'accent-secondary': 'var(--theme-accent-secondary)',
          'accent-success': 'var(--theme-accent-success)',
          'code-text': 'var(--theme-code-text)',
          'code-bg': 'var(--theme-code-bg)',
          'link': 'var(--theme-link-color)',
          'link-hover': 'var(--theme-link-hover)',
        }
      },
      animation: {
        'theme-transition': 'theme-transition 0.2s ease-in-out',
      },
      keyframes: {
        'theme-transition': {
          '0%': { opacity: '0.8' },
          '100%': { opacity: '1' },
        },
      },
    },
  },
  plugins: [
    require('@tailwindcss/typography'),
    // Custom plugin for theme-aware utilities
    function({ addUtilities, addComponents }) {
      addUtilities({
        '.theme-transition': {
          'transition': 'background-color 0.2s ease-in-out, border-color 0.2s ease-in-out, color 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
        },
      });

      addComponents({
        '.theme-card': {
          'background': 'linear-gradient(135deg, var(--theme-bg-primary) 0%, var(--theme-bg-secondary) 100%)',
          'border': '1px solid var(--theme-border-primary)',
          'border-radius': '0.75rem',
          'padding': '1.5rem',
          'transition': 'all 0.3s ease',
          'box-shadow': '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
          '&:hover': {
            'transform': 'translateY(-2px)',
            'box-shadow': '0 10px 25px rgba(15, 118, 110, 0.1)',
          },
        },
        '.theme-button': {
          'background': 'linear-gradient(135deg, var(--theme-accent-primary) 0%, var(--theme-accent-secondary) 100%)',
          'color': 'var(--theme-text-inverse)',
          'padding': '0.75rem 1.5rem',
          'border-radius': '0.5rem',
          'border': 'none',
          'font-weight': '500',
          'transition': 'all 0.2s ease',
          '&:hover': {
            'transform': 'translateY(-1px)',
            'box-shadow': '0 4px 12px rgba(15, 118, 110, 0.2)',
          },
        },
        '.theme-link': {
          'color': 'var(--theme-link-color)',
          'text-decoration': 'none',
          'font-weight': '500',
          'border-bottom': '1px dotted var(--theme-accent-primary)',
          'transition': 'all 0.2s ease',
          '&:hover': {
            'color': 'var(--theme-link-hover)',
            'border-bottom-color': 'var(--theme-accent-secondary)',
          },
        },
      });
    },
  ],
}

---
// Mermaid Theme Integration Component
// This component provides client-side Mermaid theme switching
---

<script>
  import mermaid from 'mermaid';

  // Mermaid theme configuration based on current theme
  function getMermaidConfig(isDark: boolean) {
    return {
      startOnLoad: true,
      theme: isDark ? 'dark' : 'base',
      themeVariables: {
        // Updated theme variables to match our teal/slate design
        primaryColor: isDark ? '#60a5fa' : '#0f766e',
        primaryTextColor: isDark ? '#f9fafb' : '#1e293b',
        primaryBorderColor: isDark ? '#374151' : '#e2e8f0',
        lineColor: isDark ? '#4b5563' : '#cbd5e1',
        sectionBkgColor: isDark ? '#1f2937' : '#f8fafc',
        altSectionBkgColor: isDark ? '#374151' : '#f1f5f9',
        gridColor: isDark ? '#475569' : '#e2e8f0',
        textColor: isDark ? '#f9fafb' : '#1e293b',
        taskBkgColor: isDark ? '#1f2937' : '#f8fafc',
        taskTextColor: isDark ? '#f9fafb' : '#1e293b',
        taskTextLightColor: isDark ? '#d1d5db' : '#64748b',
        taskTextOutsideColor: isDark ? '#f9fafb' : '#1e293b',
        taskTextClickableColor: isDark ? '#60a5fa' : '#0f766e',
        activeTaskBkgColor: isDark ? '#60a5fa' : '#0f766e',
        activeTaskBorderColor: isDark ? '#60a5fa' : '#0f766e',
        section0: isDark ? '#1f2937' : '#f8fafc',
        section1: isDark ? '#374151' : '#f1f5f9',
        section2: isDark ? '#334155' : '#e2e8f0',
        section3: isDark ? '#1e293b' : '#f8fafc',
        // Node colors for flowcharts  
        secondaryColor: isDark ? '#a78bfa' : '#14b8a6',
        tertiaryColor: isDark ? '#34d399' : '#10b981',
        // Background and text for nodes
        background: isDark ? '#111827' : '#ffffff',
        mainBkg: isDark ? '#1f2937' : '#f8fafc',
        secondBkg: isDark ? '#374151' : '#f1f5f9',
        tertiaryBkg: isDark ? '#334155' : '#e2e8f0',
      },
      securityLevel: 'loose',
      fontFamily: 'Inter, system-ui, sans-serif',
      fontSize: 14,
    };
  }

  // Initialize Mermaid with current theme
  function initializeMermaid() {
    const isDark = document.documentElement.classList.contains('dark');
    const config = getMermaidConfig(isDark);

    mermaid.initialize(config);

    // Find code blocks with mermaid language and convert them to diagrams
    const mermaidCodeBlocks = document.querySelectorAll('pre[data-language="mermaid"] code, pre code[data-language="mermaid"], code[class*="language-mermaid"]');
    mermaidCodeBlocks.forEach((codeElement, index) => {
      const graphDefinition = codeElement.textContent || codeElement.innerHTML;
      if (graphDefinition && graphDefinition.trim()) {
        // Create a container for the mermaid diagram
        const mermaidContainer = document.createElement('div');
        mermaidContainer.className = 'mermaid-diagram';
        mermaidContainer.style.cssText = `
          display: flex;
          justify-content: center;
          margin: 1.5rem 0;
          padding: 1rem;
          background-color: var(--theme-bg-code);
          border: 1px solid var(--theme-border-primary);
          border-radius: 0.5rem;
          transition: background-color 0.2s ease-in-out, border-color 0.2s ease-in-out;
          overflow-x: auto;
        `;

        // Create a unique ID for this diagram
        const diagramId = `mermaid-diagram-${index}`;
        mermaidContainer.id = diagramId;

        // Replace the code block with the mermaid container
        const preElement = codeElement.closest('pre');
        if (preElement && preElement.parentNode) {
          preElement.parentNode.insertBefore(mermaidContainer, preElement);
          preElement.remove();
        }

        // Render the diagram
        mermaid.render(diagramId + '-svg', graphDefinition).then(({ svg }) => {
          mermaidContainer.innerHTML = svg;
        }).catch((error) => {
          console.error('Mermaid rendering error:', error);
          // Fallback: restore original code block
          mermaidContainer.innerHTML = `<pre><code class="language-mermaid">${graphDefinition}</code></pre>`;
        });
      }
    });

    // Also handle any existing .mermaid elements (for compatibility)
    const mermaidElements = document.querySelectorAll('.mermaid');
    mermaidElements.forEach((element, index) => {
      const graphDefinition = element.textContent || element.innerHTML;
      if (graphDefinition && graphDefinition.trim()) {
        // Clear the element and re-render
        element.innerHTML = '';
        element.removeAttribute('data-processed');

        // Create a unique ID for this diagram
        const diagramId = `mermaid-existing-${index}`;
        element.id = diagramId;

        // Render the diagram
        mermaid.render(diagramId, graphDefinition).then(({ svg }) => {
          element.innerHTML = svg;
        }).catch((error) => {
          console.error('Mermaid rendering error:', error);
          // Fallback: restore original content
          element.innerHTML = `<pre><code>${graphDefinition}</code></pre>`;
        });
      }
    });
  }

  // Function to handle theme changes
  function handleThemeChange() {
    // Small delay to ensure theme class has been applied
    setTimeout(initializeMermaid, 100);
  }

  // Initialize on page load
  document.addEventListener('DOMContentLoaded', initializeMermaid);

  // Listen for theme changes
  // We'll observe changes to the 'dark' class on the document element
  const observer = new MutationObserver((mutations) => {
    mutations.forEach((mutation) => {
      if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
        const target = mutation.target as HTMLElement;
        if (target === document.documentElement) {
          handleThemeChange();
        }
      }
    });
  });

  // Start observing
  observer.observe(document.documentElement, {
    attributes: true,
    attributeFilter: ['class']
  });

  // Also listen for storage events (in case theme is changed in another tab)
  window.addEventListener('storage', (e) => {
    if (e.key === 'theme') {
      handleThemeChange();
    }
  });

  // Custom event listener for manual theme changes
  document.addEventListener('theme-changed', handleThemeChange);
</script>

<style>
  /* Enhanced Mermaid diagram styling */
  .mermaid {
    display: flex;
    justify-content: center;
    margin: 1.5rem 0;
    padding: 1rem;
    background-color: var(--theme-bg-code);
    border: 1px solid var(--theme-border-primary);
    border-radius: 0.5rem;
    transition: background-color 0.2s ease-in-out, border-color 0.2s ease-in-out;
    overflow-x: auto;
  }

  .mermaid svg {
    max-width: 100%;
    height: auto;
    font-family: 'Inter', system-ui, sans-serif;
  }

  /* Responsive adjustments */
  @media (max-width: 640px) {
    .mermaid {
      margin: 1rem -1rem;
      border-radius: 0;
      border-left: none;
      border-right: none;
    }
  }
</style>

